import React from "react"

export default function PokeView(props) {
    let mon = props.pokemon

    function determineColor() {
        if (mon.type_1 === "normal") {
            return {
                backgroundColor: "black",
                color: "white"
            };
        } else {
            return {
                backgroundColor: "grey",
                color: "blue"
            };
        }
    }

    return (
        <div className="pokeview">
            <div>
                <h1 className="pokemonName">{mon.name}</h1>
                <img src={mon.img} />
                <span className="type" style={{determineColor}}>{mon.type_1.charAt(0).toUpperCase() + mon.type_1.slice(1)}</span>
                {mon.type_2 !== "nil" && <span className="type">{mon.type_2.charAt(0).toUpperCase() + mon.type_2.slice(1)}</span>}
            </div>
            <div className="stats">
                <h1>HP: {mon.hp}</h1>
                <h1>Attack: {mon.attack}</h1>
                <h1>Defense: {mon.defense}</h1>
                <h1>Special Attack: {mon.special_attack}</h1>
                <h1>Special Defense: {mon.special_defense}</h1>
                <h1>Speed: {mon.speed}</h1>
                <h1>BST: {`${mon.hp + 
                            mon.attack +
                            mon.defense +
                            mon.special_attack +
                            mon.special_defense +
                            mon.speed}`}</h1>
            </div>
        </div>
    )
}
