import React from "react"
import { useState } from "react"
import Axios from "axios"
import PokeView from "./PokeView"

export default function Main() {
    const [pokemonName, setPokemonName] = useState("")
    const [pokemon, setPokemon] = useState({
        name: "GROVYLE",
        img: "https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/other/official-artwork/253.png"
    })

    function handleChange(event) {
        setPokemonName(event.target.value)
    }

    function searchPokemon(event) {
        event.preventDefault()
        Axios.get(`https://pokeapi.co/api/v2/pokemon/${pokemonName}`)
            .then((response) => {
                // only interested in the data
                setPokemon({
                    name: pokemonName.toUpperCase(),
                    img: response.data.sprites.other['official-artwork'].front_default,
                    hp: response.data.stats[0].base_stat,
                    attack: response.data.stats[1].base_stat,
                    defense: response.data.stats[2].base_stat,
                    special_attack: response.data.stats[0].base_stat,
                    special_defense: response.data.stats[0].base_stat,
                    speed: response.data.stats[0].base_stat,
                })
            })
            .catch((error) => {
                // have to catch errors (needed documentation for this one!)
                alert("No such Pokémon exists!")
                console.log(error)
            })
    }

    return (
        <div>
            <div className="header">
                <h1>pokeSearch</h1>
                <form>
                    <input 
                        className="input"
                        type="text"
                        name="pokemonName"
                        placeholder="Enter Pokémon's name here..."
                        value={pokemonName}
                        onChange={handleChange}
                    />
                    <button
                        className="submit-button"
                        onClick={searchPokemon}
                    >Search</button>
                </form>
            </div>
            <PokeView pokemon={pokemon}/>
        </div>
    )
}
