import React from "react"

export default function Stats(props) {

    // object destructuring coming in handy?
    let {hp, attack, defense, special_attack, special_defense, speed} 
    = props.stats

    function statsStyle(stat, color) {
        return {
            color: "white",
            marginLeft: "10px",
            textIndent: "5px",
            backgroundColor: color,
            width: `${stat*3}px`,
            borderRadius: "4px",
        }
    }

    return (
        <div className="statsview">
            <h1>HP: <span style={statsStyle(hp, "red")}>{hp}</span></h1>
            <h1>Attack: <span stlye={statsStyle(hp, "orange")}>{attack}</span></h1>
            <h1>Defense: {defense}</h1>
            <h1>Special Attack: {special_attack}</h1>
            <h1>Special Defense: {special_defense}</h1>
            <h1>Speed: {speed}</h1>
            <h1>BST: {`${hp + 
                        attack +
                        defense +
                        special_attack +
                        special_defense +
                        speed}`}</h1>
        </div>
    )
}
